import React, { useEffect } from 'react';
import { Question, AnswerCircle1, AnswerCircle2, AnswerCircle3, QuestionFace} from '../../assets/svg';
import styles from './game.module.css';
import { Context } from '../../context/Context';
import { useContext } from 'react';

const Game = () => {

const {tour,points,questionCount,createQuestions,operator,questions, checkAnswer, btnId,boardQuestion, bgColor} = useContext(Context);

useEffect(() => {
  createQuestions(operator);
},[])


const [fillObj, setFillObj] = useState({ a1: 'white', a2: 'white', a3: 'white' });

const changeFillColors = (switchMain, switchKey1, switchKey2, colorName) => {
  switch (switchMain) {
    case switchKey1:
      setFillObj(prevState => ({ ...prevState, a1: colorName }));
      break;
    case switchKey2:
      setFillObj(prevState => ({ ...prevState, a2: colorName }));
      break;
    default:
      setFillObj(prevState => ({ ...prevState, a3: colorName }));
  }
};

useEffect(() => {
  if (btnId !== null) {
    changeFillColors(clickBtnId, 0, 1, 'black');

    if (questions[questionCount]?.result !== questions[questionCount]?.choices[btnId]) {
      changeFillColors(questions[questionCount]?.result, questions[questionCount]?.choices[0], questions[questionCount]?.choices[1], 'green');
    }

  } else {
    setFillObj({ a1: 'white', a2: 'white', a3: 'white' });
  }
}, [btnId]);


  return (
    <div style = {{backgroundColor : bgColor}} className={styles.game}>
        <div className={styles.header}>
            <h3>Puan :{points}</h3>
            <h3>Tur :{tour}</h3>
            <h3>Soru :{questionCount}</h3>
        </div>
        <div >
            <Question text={boardQuestion}/>
            <QuestionFace />
            <AnswerCircle1 fill={fillObj.a1} checkAnswer={checkAnswer} text={questions[questionCount]?.choices[0]}/>
            <AnswerCircle2 fill={fillObj.a2} text={questions[questionCount]?.choices[1]}/>
            <AnswerCircle3 fill={fillObj.a3} text={questions[questionCount]?.choices[2]}/>
        </div>
    </div>
  )
}

export default Game;
