import React, { useEffect } from 'react';
import { Question, AnswerCircle1, AnswerCircle2, AnswerCircle3, QuestionFace } from '../../assets/svg';
import styles from './game.module.css';
import { Context } from '../../context/Context';
import { useContext } from 'react';

const Game = () => {

const {tour,points,questionCount,createQuestions,operator,questions, checkAnswer, btnId} = useContext(Context);

useEffect(() => {
  createQuestions(operator);
},[])

let symbol;

switch(operator){
  case 'add':
    symbol = '+'
  break;
  case 'substract':
    symbol = '-'
  break;
  case 'multiply' :
    symbol = 'x'
  break;
  case 'divide' :
    symbol = '/'
  break;
}




const boardQuestion = `${questions[questionCount]?.num1} ${symbol} ${questions[questionCount]?.num2}`

  return (
    <div className={styles.game}>
        <div className={styles.header}>
            <h3>Puan :{points}</h3>
            <h3>Tur :{tour}</h3>
            <h3>Soru :{questionCount}</h3>
        </div>
        <div >
            <Question text={boardQuestion}/>
            <QuestionFace />
            <AnswerCircle1 fill={btnId === 1 ? "green" : "white"}checkAnswer={checkAnswer} text={questions[questionCount]?.choices[0]}/>
            <AnswerCircle2 text={questions[questionCount]?.choices[1]}/>
            <AnswerCircle3 text={questions[questionCount]?.choices[2]}/>
        </div>
    </div>
  )
}

export default Game;
