import React, { createContext } from 'react'
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
export const Context = createContext();


const Provider = (props) => {

    const [operator,setOperator] = useState("");
    const [points,setPoints] = useState(0);
    const [tour,setTour] = useState(0);
    const [currentQuestion,setCurrentQuestion] = useState({});
    const [choices,setChoices] = useState([]);
    const [questions,setQuestions] = useState([]);
    const [questionCount, setQuestionCount] = useState(0);

    const navigate = useNavigate();

    const startGame = () => {
        if(!operator){
        return;
        }

        if(operator){
            setTour(tour+1);
            navigate ('/play');
        }
    };



    const createQuestions = (operator) => {
        const newArr = []; 

        // Add quiz questions to the array depending on the operator.
        // This creates 10 questions for selected operator.
        for (let i = 0; i < 10; i++) {
          if (operator === 'add') {
            newArr.push(addQuestion('add'));
          } else if (operator === 'substract') {
            // newArr.push(substractQuestion());
          } else if (operator === 'multiply') {
            // newArr.push(multiplyQuestion());
          } else if (operator === 'divide') {
            // newArr.push(divideQuestion());
          }
        }

        setQuestions(newArr);
    };

    const randomNumber = (lowerLimit,upperLimit) => {
        return Math.floor(Math.random()*upperLimit)+lowerLimit;
    };



    //creating addition questions


    const addQuestion = (operator) => {
        let num1 = randomNumber(1,98);
        let num2 = randomNumber(1,98-num1);
        let result = num1 + num2;


        let choicesArr=[result-randomNumber(1,5),result-randomNumber(6,10),result];
        console.log(choicesArr);
        choicesArr = choicesArr.sort(() => Math.random() - 0.5);
        console.log(choicesArr);
        setChoices(choicesArr);

        let createdQuestion = { num1,operator,num2,result};
        setCurrentQuestion(createdQuestion);

        return createdQuestion;
    }

    console.log(questions);

    console.log(choices);


    return(
        <Context.Provider 
        value={{
            operator,
            setOperator, 
            startGame,
            points,
            setPoints,
            tour,
            setTour,
            questions,
            setQuestions,
            questionCount,
            setQuestionCount,
            createQuestions,
            choices
        }}>
            {props.children}
        </Context.Provider>
    )
}

export default Provider;
