import React from 'react'
import { ResultLine, StartCircle, QuestionLine } from '../../assets/svg';
import styles from './result.module.css'
import { useNavigate } from 'react-router-dom';
import { useContext } from 'react';
import { Context } from '../../context/Context';


const Result = () => {

  const {setQuestionCount, trueAnswerCount, points} = useContext(Context);

  const navigate = useNavigate();

  const returnStart = () => {
    setQuestionCount(0);
    navigate('/');
  }





  return (
    <div className={styles.resultContent}>
        <div className={styles.results}>
            <h1>Sonuç</h1>
            <ResultLine style ={styles.line}/>
            <h2>Puan : {points} </h2>
            <h2>Doğru Cevap : {trueAnswerCount}</h2>
            <h2>Yanlış Cevap : {10-trueAnswerCount}</h2>
            <h3>Başa Dön</h3>
            <StartCircle onClick={returnStart} style={styles.circle}/>
        </div>
        <div className={styles.questions}>
            <h1>Sorular</h1>
            <QuestionLine/>
        </div>
    </div>
  )
}

export default Result;
