import React, { useEffect } from 'react';
import { Question, AnswerCircle1, AnswerCircle2, AnswerCircle3, QuestionFace } from '../../assets/svg';
import styles from './game.module.css';
import { Context } from '../../context/Context';
import { useContext } from 'react';

const Game = () => {

const {tour,points,questionCount,createQuestions,operator,choices,questions} = useContext(Context);

useEffect(() => {
  createQuestions(operator);
},[])


const boardQuestion = `${questions[questionCount].num1} ${questions[questionCount].operator} ${questions[questionCount].num1} = ?`


  return (
    <div className={styles.game}>
        <div className={styles.header}>
            <h3>Puan :{points}</h3>
            <h3>Tur :{tour}</h3>
            <h3>Soru :{questionCount}</h3>
        </div>
        <div >
            <Question/>
            <QuestionFace/>
            <AnswerCircle1 text={choices[1]}/>
            <AnswerCircle2 text={choices[2]}/>
            <AnswerCircle3 text={choices[0]}/>
        </div>
    </div>
  )
}

export default Game;
